  const fetchDataForDate = async (date) => {
    setLoading(true);
    try {
      // Replace with actual API call to Python backend
      const response = await fetch(`/api/trends?date=${date}`);
      const data = await response.json();
      
      setTrendData(data.trends);
      setArxivLinks(data.articles);
      setBlogPost(data.blogPost);
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const sendChatMessage = async () => {
    if (!chatInput.trim()) return;

    const userMessage = { role: 'user', content: chatInput };
    setChatMessages(prev => [...prev, userMessage]);
    setChatInput('');
    setLoading(true);

    try {
      // Replace with actual API call to Python backend
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          message: chatInput,
          context: { date: selectedDate, articles: arxivLinks }
        })
      });
      
      const data = await response.json();
      const assistantMessage = { role: 'assistant', content: data.response };
      setChatMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('Error sending message:', error);
      setChatMessages(prev => [...prev, { 
        role: 'assistant', 
        content: 'Sorry, I encountered an error. Please try again.' 
      }]);
    } finally {
      setLoading(false);
    }
  };

  const handleDateSubmit = () => {
    fetchDataForDate(selectedDate);
  };

  const colors = ['#8884d8', '#82ca9d', '#ffc658', '#ff7c7c', '#8dd1e1'];
  const keywords = ['Transformers', 'Diffusion Models', 'Reinforcement Learning', 'Computer Vision', 'NLP'];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 text-white">
      {/* Header */}
      <div className="bg-black/20 backdrop-blur-sm border-b border-purple-500/30">
        <div className="container mx-auto px-6 py-4">
          <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent flex items-center gap-3">
            <TrendingUp className="text-blue-400" size={36} />
            ML Research Trend Tracker
          </h1>
          <p className="text-slate-300 mt-2">Real-time analysis of machine learning research trends</p>
        </div>
      </div>

      <div className="container mx-auto px-6 py-8">
        {/* Main Grid Layout */}
        <div className="grid grid-cols-12 gap-6 h-[calc(100vh-200px)]">
          
          {/* Left Panel - ArXiv Links */}
          <div className="col-span-3 bg-slate-800/50 backdrop-blur-sm rounded-xl border border-slate-700/50 p-6">
            <h2 className="text-xl font-semibold mb-4 text-blue-300 flex items-center gap-2">
              <ExternalLink size={20} />
              Featured Papers
            </h2>
            <div className="space-y-3">
              {arxivLinks.map((paper, index) => (
                <div key={paper.id} className="group">
                  <a 
                    href={paper.url} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="block p-3 rounded-lg bg-slate-700/30 hover:bg-slate-600/40 transition-all duration-200 border border-slate-600/30 hover:border-blue-400/50"
                  >
                    <div className="text-sm font-medium text-blue-300 group-hover:text-blue-200 leading-tight">
                      {paper.title}
                    </div>
                    <div className="text-xs text-slate-400 mt-1">arXiv:{paper.id}</div>
                  </a>
                </div>
              ))}
            </div>
          </div>

          {/* Top Right - Trend Chart */}
          <div className="col-span-6 bg-slate-800/50 backdrop-blur-sm rounded-xl border border-slate-700/50 p-6">
            <h2 className="text-xl font-semibold mb-4 text-green-300">Research Trends</h2>
            <ResponsiveContainer width="100%" height="90%">
              <LineChart data={trendData}>
                <CartesianGrid strokeDasharray="3 3" stroke="#374151" />
                <XAxis 
                  dataKey="date" 
                  stroke="#9CA3AF"
                  tick={{ fontSize: 12 }}
                />
                <YAxis 
                  stroke="#9CA3AF"
                  tick={{ fontSize: 12 }}
                />
                <Tooltip 
                  contentStyle={{ 
                    backgroundColor: '#1F2937', 
                    border: '1px solid #374151',
                    borderRadius: '8px',
                    color: 'white'
                  }}
                />
                <Legend />
                {keywords.map((keyword, index) => (
                  <Line 
                    key={keyword}
                    type="monotone" 
                    dataKey={keyword} 
                    stroke={colors[index]}
                    strokeWidth={2}
                    dot={{ fill: colors[index], strokeWidth: 2, r: 4 }}
                  />
                ))}
              </LineChart>
            </ResponsiveContainer>
          </div>

          {/* Right Panel - Chatbot */}
          <div className="col-span-3 bg-slate-800/50 backdrop-blur-sm rounded-xl border border-slate-700/50 p-6 flex flex-col">
            <h2 className="text-xl font-semibold mb-4 text-purple-300">Research Assistant</h2>
            
            {/* Chat Messages */}
            <div className="flex-1 overflow-y-auto space-y-3 mb-4">
              {chatMessages.map((message, index) => (
                <div key={index} className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                  <div className={`max-w-[80%] p-3 rounded-lg text-sm ${
                    message.role === 'user' 
                      ? 'bg-blue-600 text-white' 
                      : 'bg-slate-700 text-slate-200'
                  }`}>
                    {message.content}
                  </div>
                </div>
              ))}
              {loading && (
                <div className="flex justify-start">
                  <div className="bg-slate-700 text-slate-200 p-3 rounded-lg text-sm">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                      <div className="w-2 h-2 bg-slate-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Chat Input */}
            <div className="flex gap-2">
              <input
                type="text"
                value={chatInput}
                onChange={(e) => setChatInput(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && sendChatMessage()}
                placeholder="Ask about the research..."
                className="flex-1 px-3 py-2 bg-slate-700 border border-slate-600 rounded-lg text-white placeholder-slate-400 focus:outline-none focus:border-purple-400"
              />
              <button
                onClick={sendChatMessage}
                disabled={loading || !chatInput.trim()}
                className="px-3 py-2 bg-purple-600 hover:bg-purple-700 disabled:bg-slate-600 text-white rounded-lg transition-colors duration-200"
              >
                <Send size={16} />
              </button>
            </div>
          </div>

          {/* Bottom Panel - Blog Post */}
          <div className="col-span-9 bg-slate-800/50 backdrop-blur-sm rounded-xl border border-slate-700/50 p-6 overflow-y-auto">
            <h2 className="text-xl font-semibold mb-4 text-yellow-300">Daily Research Summary</h2>
            <div className="prose prose-invert max-w-none">
              <pre className="whitespace-pre-wrap text-slate-200 leading-relaxed font-sans">{blogPost}</pre>
            </div>
          </div>

          {/* Bottom Right - Date Selector */}
          <div className="col-span-3 bg-slate-800/50 backdrop-blur-sm rounded-xl border border-slate-700/50 p-6">
            <h2 className="text-xl font-semibold mb-4 text-orange-300 flex items-center gap-2">
              <Calendar size={20} />
              Time Travel
            </h2>
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-slate-300 mb-2">
                  Select Date
                </label>
                <input
                  type="date"
                  value={selectedDate}
                  onChange={(e) => setSelectedDate(e.target.value)}
                  className="w-full px-3 py-2 bg-slate-700 border border-slate-600 rounded-lg text-white focus:outline-none focus:border-orange-400"
                />
              </div>
              <button
                onClick={handleDateSubmit}
                disabled={loading}
                className="w-full px-4 py-2 bg-orange-600 hover:bg-orange-700 disabled:bg-slate-600 text-white rounded-lg font-medium transition-colors duration-200"
              >
                {loading ? 'Loading...' : 'Go to Date'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MLResearchTracker;
